import React, {useState} from 'react';

import "./index.css"

export default function App() {
  const [wells, setWells] = useState(getWells());

  function getWells() {
    let wells = [];
    for (let i = 0; i < 8; i++) {
      for (let j = 0; j < 12; j++) {
        wells.push({
          col: j,
          row: i,
          selected: false,
        });
      }
    }
    return wells;
  }

  return (
    <div className="App">
      <div
        // ref={wellsContainer}
        //    onMouseDown={e => mouseDownListener(e)}
        //    onMouseMove={e => mouseMoveListener(e)}
        //    onMouseUp={mouseUpListener}
      >
        {
          wells.map(well => (
            <div key={well.row * 12 + well.col}
                 style={{borderStyle: well.selected ? "dashed" : "solid"}}
            >
            </div>
          ))
        }
      </div>
    </div>
  );
}
